import React, { useEffect, useState } from 'react';
import Styles from './TODO.module.css';
import axios from 'axios';

export function TODO(props) {
    const [newTodo, setNewTodo] = useState('');
    const [todoData, setTodoData] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchTodo();
    }, []);

    const fetchTodo = async () => {
        try {
            const response = await axios.get('http://localhost:8000/api/todo');
            setTodoData(response.data);
            setLoading(false);
        } catch (err) {
            console.log(err);
        }
    };

    const addTodo = async () => {
        try {
            const response = await axios.post('http://localhost:8000/api/todo', { title: newTodo });
            setTodoData(prevData => [...prevData, response.data]);
            setNewTodo('');
        } catch (err) {
            console.log(err);
        }
    };

    const deleteTodo = async (id) => {
        try {
            await axios.delete(`http://localhost:8000/api/todo/${id}`);
            setTodoData(prevData => prevData.filter(todo => todo._id !== id));
        } catch (err) {
            console.log(err);
        }
    };

    const updateTodo = async (id, newData) => {
        try {
            const response = await axios.patch(`http://localhost:8000/api/todo/${id}`, newData);
            setTodoData(prevData => prevData.map(todo => (todo._id === id ? response.data : todo)));
        } catch (err) {
            console.log(err);
        }
    };

    const [editTodoId, setEditTodoId] = useState(null);
    const [editTodoTitle, setEditTodoTitle] = useState('');
    const [editTodoDescription, setEditTodoDescription] = useState('');

    const handleEditTodo = (todo) => {
        setEditTodoId(todo._id);
        setEditTodoTitle(todo.title);
        setEditTodoDescription(todo.description);
    };

    const saveEditedTodo = async (id) => {
        try {
            await updateTodo(id, { title: editTodoTitle, description: editTodoDescription });
            setEditTodoId(null);
            setEditTodoTitle('');
            setEditTodoDescription('');
        } catch (err) {
            console.log(err);
        }
    };

    return (
        <div className={Styles.ancestorContainer}>
            <div className={Styles.headerContainer}>
                <h1>Tasks</h1>
                <span>
                    <input
                        className={Styles.todoInput}
                        type='text'
                        name='New Todo'
                        value={newTodo}
                        onChange={(event) => setNewTodo(event.target.value)}
                    />
                    <button
                        id='addButton'
                        name='add'
                        className={Styles.addButton}
                        onClick={addTodo}
                    >
                        + New Todo
                    </button>
                </span>
            </div>
            <div id='todoContainer' className={Styles.todoContainer}>
                {loading ? (
                    <p style={{ color: 'white' }}>Loading...</p>
                ) : todoData.length > 0 ? (
                    todoData.map((entry, index) => (
                        <div key={entry._id} className={Styles.todo}>
                            {editTodoId === entry._id ? (
                                <>
                                    <input
                                        type='text'
                                        value={editTodoTitle}
                                        onChange={(e) => setEditTodoTitle(e.target.value)}
                                    />
                                    <textarea
                                        value={editTodoDescription}
                                        onChange={(e) => setEditTodoDescription(e.target.value)}
                                    />
                                    <button onClick={() => saveEditedTodo(entry._id)}>Save</button>
                                </>
                            ) : (
                                <>
                                    <span className={Styles.infoContainer}>
                                        <input
                                            type='checkbox'
                                            checked={entry.done}
                                            onChange={() => updateTodo(entry._id, { done: !entry.done })}
                                        />
                                        {entry.title}
                                    </span>
                                    <span>{entry.description}</span>
                                    <span
                                        style={{ cursor: 'pointer' }}
                                        onClick={() => handleEditTodo(entry)}
                                    >
                                        Edit
                                    </span>
                                </>
                            )}
                            <span
                                style={{ cursor: 'pointer' }}
                                onClick={() => deleteTodo(entry._id)}
                            >
                                Delete
                            </span>
                        </div>
                    ))
                ) : (
                    <p className={Styles.noTodoMessage}>No tasks available. Please add a new task.</p>
                )}
            </div>
        </div>
    );
}
